// client/src/components/AdminControls.js
import React, { useState } from 'react';

const AdminControls = ({ pageNumber, numPages, changePage, onUpload }) => {
  const [file, setFile] = useState(null);

  const handleFileChange = (e) => {
    const uploadedFile = e.target.files[0];
    if (uploadedFile) {
      setFile(uploadedFile);
      onUpload(uploadedFile);
    }
  };

  const goToPage = (e) => {
    const page = parseInt(e.target.value, 10);
    if (page >= 1 && page <= numPages) {
      changePage(page - pageNumber);
    }
  };

  return (
    <div className="admin-controls">
      <button onClick={() => changePage(-1)} disabled={pageNumber <= 1}>Previous</button>
      <span> Page {pageNumber} of {numPages} </span>
      <button onClick={() => changePage(1)} disabled={pageNumber >= numPages}>Next</button>
      
      <input
        type="number"
        value={pageNumber}
        onChange={goToPage}
        min="1"
        max={numPages}
        style={{ width: '60px', marginLeft: '10px' }}
      />
      
      <input
        type="file"
        accept="application/pdf"
        onChange={handleFileChange}
        style={{ marginLeft: '10px' }}
      />
      {file && <span>Uploaded: {file.name}</span>}
    </div>
  );
};

export default AdminControls;
