import React from 'react';
import AdminControls from './components/AdminControls';
import Viewer from './components/Viewer';
import { SocketProvider } from './context/SocketContext';

const App = () => (
  <SocketProvider>
    <h1>Synchronized Page Viewer</h1>
    <AdminControls />  {/* Admin controls the page number */}
    <Viewer />         {/* Viewer listens for page updates */}
  </SocketProvider>
);

export default App;
