// client/src/App.js
import React, { useState } from 'react';
import './App.css';
import AdminControls from './components/AdminControls';
import Viewer from './components/Viewer';
import { SocketProvider } from './context/SocketContext';

function App() {
  const [isAdmin, setIsAdmin] = useState(false);

  return (
    <SocketProvider>
      <div className="App">
        <h1>PDF Co-Viewer</h1>
        {isAdmin ? (
          <div>
            <AdminControls />
            <button onClick={() => setIsAdmin(false)}>Switch to Viewer</button>
          </div>
        ) : (
          <div>
            <Viewer />
            <button onClick={() => setIsAdmin(true)}>Switch to Admin</button>
          </div>
        )}
      </div>
    </SocketProvider>
  );
}

export default App;
