// client/src/App.js
import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:4000'); // Connect to the backend server

function App() {
  const [page, setPage] = useState(1); // Initialize page to 1
  const [isAdmin, setIsAdmin] = useState(false); // Toggle between admin and viewer

  // Listen for the sync-page event from the server to update the page
  useEffect(() => {
    socket.on('sync-page', (newPage) => {
      setPage(newPage); // Update page when the server sends the sync-page event
    });

    return () => {
      socket.off('sync-page'); // Clean up the socket listener
    };
  }, []);

  // Function to handle page change when the admin clicks "Next" or "Previous"
  const handlePageChange = (direction) => {
    const newPage = direction === 'next' ? page + 1 : page - 1;
    setPage(newPage); // Update local page
    socket.emit('change-page', newPage); // Notify the server of the page change
  };

  return (
    <div className="App">
      <h1>Current Page: {page}</h1>
      <div>
        {isAdmin ? (
          <div>
            <button onClick={() => handlePageChange('prev')}>Previous</button>
            <button onClick={() => handlePageChange('next')}>Next</button>
          </div>
        ) : (
          <p>Viewer Mode</p>
        )}
        <button onClick={() => setIsAdmin(!isAdmin)}>
          Toggle to {isAdmin ? 'Viewer' : 'Admin'} Mode
        </button>
      </div>
    </div>
  );
}

export default App;
