// client/src/components/Admin.js
import React, { useState } from 'react';
import useWebSocket from '../hooks/useWebSocket';

const Admin = () => {
    const { sendMessage } = useWebSocket('ws://localhost:8080');
    const [message, setMessage] = useState('');
    const [currentPage, setCurrentPage] = useState(1);

    const handleSendMessage = () => {
        if (message) {
            sendMessage({ type: 'adminMessage', payload: message });
            setMessage('');
        }
    };

    const handlePageChange = (page) => {
        setCurrentPage(page);
        sendMessage({ type: 'pageChange', page });
    };

    return (
        <div>
            <h1>Admin Page</h1>
            <input
                type="text"
                placeholder="Type a message"
                value={message}
                onChange={(e) => setMessage(e.target.value)}
            />
            <button onClick={handleSendMessage}>Send Message</button>
            <h2>Change Page:</h2>
            <button onClick={() => handlePageChange(1)}>Page 1</button>
            <button onClick={() => handlePageChange(2)}>Page 2</button>
            <button onClick={() => handlePageChange(3)}>Page 3</button>
        </div>
    );
};

export default Admin;
