// server/socket.js
const setupSocket = (io) => {
  io.on('connection', (socket) => {
    socket.on('joinRoom', (roomId) => {
      socket.join(roomId);
    });

    socket.on('pageChange', ({ roomId, pageNumber }) => {
      socket.to(roomId).emit('pageUpdate', pageNumber);
    });
  });
};

module.exports = { setupSocket };
