// client/src/components/Viewer.js
import React, { useEffect, useState } from 'react';
import useWebSocket from '../hooks/useWebSocket';

const Viewer = () => {
    const { messages } = useWebSocket('ws://localhost:8080'); // Hook to manage WebSocket messages
    const [currentPage, setCurrentPage] = useState(1); // State to hold the current page number

    useEffect(() => {
        // Effect to update the current page based on incoming messages
        messages.forEach((msg) => {
            if (msg.type === 'pageChange') {
                setCurrentPage(msg.page); // Update the current page when a pageChange message is received
            }
        });
    }, [messages]); // Dependency array to re-run effect when messages change

    return (
        <div>
            <h1>Viewer Page</h1>
            <h2>Current Page: {currentPage}</h2>
            <h2>Page Content for Page {currentPage}</h2>
        </div>
    );
};

export default Viewer; // Export the Viewer component
