import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:4000'); // Connecting to the backend server

function App() {
  const [page, setPage] = useState(1);
  const [isAdmin, setIsAdmin] = useState(false); // To toggle between admin and viewer

  useEffect(() => {
    // Listen for page sync events from server
    socket.on('sync-page', (newPage) => {
      setPage(newPage);
    });
  }, []);

  // Handle page navigation (for admin only)
  const handlePageChange = (direction) => {
    const newPage = direction === 'next' ? page + 1 : page - 1;
    setPage(newPage);
    socket.emit('change-page', newPage);  // Notify the server and sync with viewers
  };

  return (
    <div className="App">
      <h1>Page: {page}</h1>
      <div>
        {isAdmin ? (
          <div>
            <button onClick={() => handlePageChange('prev')}>Previous</button>
            <button onClick={() => handlePageChange('next')}>Next</button>
          </div>
        ) : (
          <p>Viewer Mode</p>
        )}
        <button onClick={() => setIsAdmin(!isAdmin)}>
          Toggle to {isAdmin ? 'Viewer' : 'Admin'} Mode
        </button>
      </div>
    </div>
  );
}

export default App;
