// client/src/components/Viewer.js
import React, { useContext } from 'react';
import { SocketContext } from '../context/SocketContext';

const Viewer = () => {
  const { currentPage } = useContext(SocketContext);

  // Placeholder to simulate different pages
  const getPageContent = (pageNum) => {
    return (
      <div>
        <h3>Page {pageNum}</h3>
        <img src={`https://via.placeholder.com/600x400?text=Page+${pageNum}`} alt={`Page ${pageNum}`} />
      </div>
    );
  };

  return (
    <div>
      <h2>Viewer Mode</h2>
      {getPageContent(currentPage)}
    </div>
  );
};

export default Viewer;
