// client/src/components/PDFViewer.js
import React, { useState, useEffect, useContext } from 'react';
import { Document, Page } from 'react-pdf';
import { SocketContext } from '../context/SocketContext';

const PDFViewer = ({ pdfUrl, isAdmin }) => {
  const socket = useContext(SocketContext);
  const [pageNumber, setPageNumber] = useState(1);

  // Handle manual page changes by admin
  const changePage = (newPage) => {
    setPageNumber(newPage);
    if (isAdmin) {
      // Emit the new page number to the server if admin
      socket.emit('page-change', newPage);
    }
  };

  // Listen for page updates from the server
  useEffect(() => {
    socket.on('update-page', (newPage) => {
      setPageNumber(newPage);
    });

    // Cleanup on component unmount
    return () => socket.off('update-page');
  }, [socket]);

  return (
    <div>
      <Document file={pdfUrl}>
        <Page pageNumber={pageNumber} />
      </Document>

      <div>
        {isAdmin && (
          <div>
            <button onClick={() => changePage(pageNumber - 1)} disabled={pageNumber <= 1}>
              Previous
            </button>
            <button onClick={() => changePage(pageNumber + 1)}>
              Next
            </button>
          </div>
        )}
        <p>Page {pageNumber}</p>
      </div>
    </div>
  );
};

export default PDFViewer;
