import React, { useEffect, useState, useContext } from 'react';
import { Worker, Viewer } from '@react-pdf-viewer/core';
import '@react-pdf-viewer/core/lib/styles/index.css';
import { SocketContext } from '../context/SocketContext';
import AdminControls from './AdminControls';

const PDFViewer = ({ isAdmin }) => {
  const [pageNumber, setPageNumber] = useState(1);
  const [numPages, setNumPages] = useState(null);
  const socket = useContext(SocketContext);

  const changePage = (offset) => {
    const newPage = pageNumber + offset;
    if (newPage >= 1 && newPage <= numPages) {
      setPageNumber(newPage);
      if (isAdmin) socket.emit('pageChange', newPage);
    }
  };

  useEffect(() => {
    if (!isAdmin) {
      socket.on('pageUpdate', (newPage) => setPageNumber(newPage));
    }
  }, [socket, isAdmin]);

  return (
    <div>
      <Worker workerUrl={`https://unpkg.com/pdfjs-dist@${pdfjs.version}/pdf.worker.min.js`}>
        <Viewer fileUrl="sample.pdf" initialPage={pageNumber - 1} />
      </Worker>
      <p>Page {pageNumber}</p>
      {isAdmin && (
        <AdminControls
          pageNumber={pageNumber}
          numPages={numPages}
          changePage={changePage}
        />
      )}
    </div>
  );
};

export default PDFViewer;
