// client/src/components/Viewer.js
import React, { useContext } from 'react';
import { SocketContext } from '../context/SocketContext';

const Viewer = () => {
  const { currentPage } = useContext(SocketContext);

  return (
    <div>
      <h2>Viewer Mode</h2>
      <p>Currently Viewing: Page {currentPage}</p>
      {/* Here you can render the actual PDF page, if needed */}
    </div>
  );
};

export default Viewer;
