// client/src/components/PDFViewer.js
import React, { useEffect, useState, useContext } from 'react';
import { Document, Page, pdfjs } from 'react-pdf';
import { SocketContext } from '../context/SocketContext';
import AdminControls from './AdminControls';

// Set the worker source for PDF.js
pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.min.js`;

const PDFViewer = ({ isAdmin }) => {
  const [pageNumber, setPageNumber] = useState(1);
  const [numPages, setNumPages] = useState(null);
  const socket = useContext(SocketContext);

  // Callback to handle successful document loading
  const onDocumentLoadSuccess = ({ numPages }) => {
    setNumPages(numPages);
  };

  // Function to change pages based on admin input
  const changePage = (offset) => {
    const newPage = pageNumber + offset;
    if (newPage >= 1 && newPage <= numPages) {
      setPageNumber(newPage);
      if (isAdmin) socket.emit('pageChange', newPage); // Broadcast page change to viewers
    }
  };

  // Sync page number with the socket (for viewers)
  useEffect(() => {
    if (!isAdmin) {
      socket.on('pageUpdate', (newPage) => setPageNumber(newPage)); // Listen to page updates
    }
  }, [socket, isAdmin]);

  return (
    <div>
      <Document file="sample.pdf" onLoadSuccess={onDocumentLoadSuccess}>
        <Page pageNumber={pageNumber} />
      </Document>
      <p>Page {pageNumber} of {numPages}</p>
      {isAdmin && (
        <AdminControls
          pageNumber={pageNumber}
          numPages={numPages}
          changePage={changePage}
        />
      )}
    </div>
  );
};

export default PDFViewer;
