// src/components/PDFViewer.js
import React, { useEffect, useState } from 'react';
import { pdfjs, Document, Page } from 'react-pdf';
import socket from '../socket';

pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;

const PDFViewer = ({ pdfFile, isAdmin }) => {
    const [numPages, setNumPages] = useState(null);
    const [pageNumber, setPageNumber] = useState(1);

    useEffect(() => {
        // Listen for page change events
        socket.on('page-change', (page) => {
            setPageNumber(page);
        });

        return () => {
            socket.off('page-change');
        };
    }, []);

    const onDocumentLoadSuccess = ({ numPages }) => {
        setNumPages(numPages);
    };

    const changePage = (offset) => {
        const newPageNumber = pageNumber + offset;
        if (newPageNumber >= 1 && newPageNumber <= numPages) {
            setPageNumber(newPageNumber);
            if (isAdmin) {
                socket.emit('admin-page-change', newPageNumber);  // Admin broadcasts page change
            }
        }
    };

    return (
        <div className="pdf-viewer">
            <Document file={pdfFile} onLoadSuccess={onDocumentLoadSuccess}>
                <Page pageNumber={pageNumber} />
            </Document>
            <div className="controls">
                <button onClick={() => changePage(-1)} disabled={pageNumber <= 1}>Previous</button>
                <span>Page {pageNumber} of {numPages}</span>
                <button onClick={() => changePage(1)} disabled={pageNumber >= numPages}>Next</button>
            </div>
        </div>
    );
};

export default PDFViewer;
