// react-pdf-co-viewer/src/App.js
import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import { pdfjs } from 'react-pdf';
import PDFViewer from './components/PDFViewer';
import Navigation from './components/Navigation';
import './App.css';

// Set the worker source for pdfjs
pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.min.js`;

const socket = io.connect('http://localhost:5000'); // Update with your backend URL if hosted

function App() {
  const [pdfFile, setPdfFile] = useState(null);
  const [pageNumber, setPageNumber] = useState(1);
  const [numPages, setNumPages] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);

  useEffect(() => {
    socket.on('slideChange', (page) => {
      if (!isAdmin) setPageNumber(page);
    });

    return () => {
      socket.off('slideChange');
    };
  }, [isAdmin]);

  const onDocumentLoadSuccess = ({ numPages }) => setNumPages(numPages);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    const formData = new FormData();
    formData.append('pdf', file);

    fetch('http://localhost:5000/upload', {
      method: 'POST',
      body: formData,
    })
      .then((res) => res.json())
      .then((data) => setPdfFile(data.path))
      .catch((error) => console.error('Error uploading file:', error));
  };

  const handlePrevious = () => {
    if (pageNumber > 1) {
      setPageNumber(pageNumber - 1);
      if (isAdmin) socket.emit('changeSlide', pageNumber - 1);
    }
  };

  const handleNext = () => {
    if (pageNumber < numPages) {
      setPageNumber(pageNumber + 1);
      if (isAdmin) socket.emit('changeSlide', pageNumber + 1);
    }
  };

  return (
    <div className="App">
      <h1>Co-Viewer PDF Application</h1>
      {isAdmin && (
        <div>
          <input type="file" onChange={handleFileUpload} accept="application/pdf" />
        </div>
      )}
      <PDFViewer pdfFile={pdfFile} pageNumber={pageNumber} onLoadSuccess={onDocumentLoadSuccess} />
      <Navigation
        pageNumber={pageNumber}
        numPages={numPages}
        onPrevious={handlePrevious}
        onNext={handleNext}
      />
      <div>
        <label>
          <input
            type="checkbox"
            checked={isAdmin}
            onChange={(e) => setIsAdmin(e.target.checked)}
          />
          Admin Mode
        </label>
      </div>
    </div>
  );
}

export default App;
