// client/src/components/AdminControls.js
import React from 'react';

const AdminControls = ({ pageNumber, numPages, changePage }) => {
  const goToPage = (e) => {
    const page = parseInt(e.target.value, 10);
    if (page >= 1 && page <= numPages) {
      changePage(page - pageNumber); // Move to the selected page
    }
  };

  return (
    <div style={{ marginTop: '20px' }}>
      <button onClick={() => changePage(-1)} disabled={pageNumber <= 1}>Previous</button>
      <span> Page {pageNumber} of {numPages} </span>
      <button onClick={() => changePage(1)} disabled={pageNumber >= numPages}>Next</button>
      
      {/* Jump to a specific page */}
      <input
        type="number"
        value={pageNumber}
        onChange={goToPage}
        min="1"
        max={numPages}
        style={{ width: '60px', marginLeft: '10px' }}
      />
    </div>
  );
};

export default AdminControls;
