// frontend/src/PDFViewer.js
import React, { useEffect, useState } from 'react';
import { io } from 'socket.io-client';
import './styles.css';

const socket = io('http://localhost:4000'); // Adjust the URL if needed

const PDFViewer = () => {
    const [currentPage, setCurrentPage] = useState(1);

    useEffect(() => {
        // Listen for page changes from the server
        socket.on('pageChanged', (page) => {
            setCurrentPage(page);
        });

        return () => {
            socket.off('pageChanged');
        };
    }, []);

    const changePage = (page) => {
        setCurrentPage(page);
        socket.emit('changePage', page); // Notify the server
    };

    return (
        <div className="pdf-viewer">
            <h1>PDF Viewer</h1>
            <iframe
                src={`https://docs.google.com/gview?url=<PDF_URL>&embedded=true`}
                style={{ width: '100%', height: '600px' }}
                title="PDF Viewer"
            />
            <div className="controls">
                <button onClick={() => changePage(currentPage - 1)} disabled={currentPage === 1}>Previous</button>
                <span>Page {currentPage}</span>
                <button onClick={() => changePage(currentPage + 1)}>Next</button>
            </div>
        </div>
    );
};

export default PDFViewer;
