import React, { useEffect, useState } from 'react';
import { useSocket } from '../context/SocketContext';

const Viewer = () => {
  const [pageNum, setPageNum] = useState(1);
  const socket = useSocket();

  useEffect(() => {
    if (socket) {
      // Listen for the 'page-change' event from the server
      socket.on('page-change', (newPageNum) => {
        setPageNum(newPageNum); // Update the page number
      });
    }

    // Cleanup the socket listener when the component is unmounted
    return () => {
      if (socket) socket.off('page-change');
    };
  }, [socket]);

  return (
    <div>
      <h2>Viewer</h2>
      <p>Current Page: {pageNum}</p>
    </div>
  );
};

export default Viewer;
