import React, { useState } from 'react';
import { useSocket } from '../context/SocketContext';

const AdminControls = () => {
  const [pageNum, setPageNum] = useState(1); // Local state for page number
  const socket = useSocket();

  // Handle page change (Next or Previous)
  const changePage = (offset) => {
    const newPageNum = pageNum + offset; // Update page number
    setPageNum(newPageNum); // Update local state

    if (socket) {
      socket.emit('page-change', newPageNum); // Emit to all clients via WebSocket
    }
  };

  return (
    <div>
      <h3>Admin Controls</h3>
      <button onClick={() => changePage(-1)} disabled={pageNum <= 1}>
        Previous
      </button>
      <button onClick={() => changePage(1)}>Next</button>
    </div>
  );
};

export default AdminControls;
