import React, { useState } from 'react';

const AdminControls = ({ socket }) => {
  const [pageNum, setPageNum] = useState(1);

  const handlePageChange = (newPageNum) => {
    if (newPageNum < 1) return; // Prevent going to invalid pages
    setPageNum(newPageNum);
    socket.emit('page-change', newPageNum); // Emit the new page number to all viewers
  };

  return (
    <div>
      <button onClick={() => handlePageChange(pageNum - 1)} disabled={pageNum <= 1}>
        Previous Page
      </button>
      <button onClick={() => handlePageChange(pageNum + 1)}>
        Next Page
      </button>
    </div>
  );
};

export default AdminControls;
