// src/App.js

import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import './App.css';

// Connect to the backend server
const socket = io('http://localhost:5000');

function App() {
  const [pageNumber, setPageNumber] = useState(1);
  const [numPages, setNumPages] = useState(10); // You can customize this to be dynamic based on content
  const [isAdmin, setIsAdmin] = useState(false);

  // Synchronize the page number from the server when a viewer joins
  useEffect(() => {
    socket.on('syncPage', ({ page }) => {
      setPageNumber(page);
    });

    socket.on('slideChange', (pageNumber) => {
      setPageNumber(pageNumber);
    });

    return () => {
      socket.off('syncPage');
      socket.off('slideChange');
    };
  }, []);

  // Handle Next and Previous buttons (Admin control)
  const handlePrevious = () => {
    if (pageNumber > 1) {
      const newPageNumber = pageNumber - 1;
      setPageNumber(newPageNumber);
      socket.emit('changeSlide', newPageNumber);
    }
  };

  const handleNext = () => {
    if (pageNumber < numPages) {
      const newPageNumber = pageNumber + 1;
      setPageNumber(newPageNumber);
      socket.emit('changeSlide', newPageNumber);
    }
  };

  return (
    <div className="App">
      <h1>Co-Viewer Page Synchronization</h1>

      {isAdmin && (
        <div>
          <button onClick={handlePrevious} disabled={pageNumber <= 1}>
            Previous
          </button>
          <span>{pageNumber} of {numPages}</span>
          <button onClick={handleNext} disabled={pageNumber >= numPages}>
            Next
          </button>
        </div>
      )}

      {/* Optionally display "Admin Mode" toggle */}
      <div>
        <label>
          <input
            type="checkbox"
            checked={isAdmin}
            onChange={(e) => setIsAdmin(e.target.checked)}
          />
          Admin Mode
        </label>
      </div>

      <div>
        <h2>Current Page: {pageNumber}</h2>
      </div>
    </div>
  );
}

export default App;
